mapper.insert:
INSERT INTO goods( name , price , amount , discountamount , discountrate , deliveryfee , sellid , image , detail , product_code , insertion_date , is_deleted ) VALUES
( #{Goods.name} , #{Goods.price} , NULL , NULL , NULL , #{Goods.deliveryfee} , #{Goods.sellid} , #{Goods.image} , #{Goods.detail} , #{Goods.product_code} , #{Goods.insertion_date}
,#{Goods.is_deleted}
,0
)
ON DUPLICATE KEY UPDATE name = values(name), price = values(price), deliveryfee = values(deliveryfee), sellid = values(sellid), image = values(image), detail = values(detail), is_deleted = CASE WHEN VALUES(is_deleted) IS NOT NULL THEN VALUES(is_deleted) ELSE is_deleted END, modification_time = SYSDATE()
